package com.bptn.course;

public class WhileLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 0; 
		while (i<=20) {
			System.out.println(i);
			i= i+2;
		}
		
		int a = 0; 
		while (a<=20) {
			System.out.println(a);
			a*=2; // to multiply output by 2 
		}
	}

}
